var Directory;

Directory = (function() {
  function Directory(name, relativePath) {
    this.name = name;
    this.relativePath = relativePath;

    /*
      		Instanciation of directory, with his name and his relative path
     */
    if (this.relativePath.charAt(this.relativePath.length - 1) !== '/') {
      this.relativePath += '/';
    }
    this.relativePath += this.name;
  }

  Directory.prototype.getRelativePath = function() {

    /*
      		Get the directory relativePath
     */
    return this.relativePath;
  };

  Directory.prototype.getName = function() {

    /*
      		Get the directory name
     */
    return this.name;
  };

  return Directory;

})();

module.exports = Directory;
