var MiddlewareDefinition;

MiddlewareDefinition = (function() {
  function MiddlewareDefinition(registeredMiddleware) {
    var middleware;
    this.registeredMiddleware = registeredMiddleware;
    middleware = this.registeredMiddleware.middleware.split(':');
    this.name = this.registeredMiddleware.name;
    this.pattern = '*';
    if (this.registeredMiddleware.pattern) {
      this.pattern = this.registeredMiddleware.pattern;
    }
    this.bundleName = middleware[0];
    this.middlewareName = middleware[1];
    this.methodName = middleware[2];
  }

  MiddlewareDefinition.prototype.setBundleName = function(bundleName) {
    this.bundleName = bundleName;
    return this;
  };

  MiddlewareDefinition.prototype.setMiddlewareName = function(middlewareName) {
    this.middlewareName = middlewareName;
    return this;
  };

  MiddlewareDefinition.prototype.setMethodName = function(methodName) {
    this.methodName = methodName;
    return this;
  };

  MiddlewareDefinition.prototype.setName = function(name) {
    this.name = name;
    return this;
  };

  MiddlewareDefinition.prototype.getBundleName = function() {
    return this.bundleName;
  };

  MiddlewareDefinition.prototype.getMiddlewareName = function() {
    return this.middlewareName;
  };

  MiddlewareDefinition.prototype.getMethodName = function() {
    return this.methodName;
  };

  MiddlewareDefinition.prototype.getName = function() {
    return this.name;
  };

  MiddlewareDefinition.prototype.setPattern = function(pattern) {
    this.pattern = pattern;
    return this;
  };

  MiddlewareDefinition.prototype.getPattern = function() {
    return this.pattern;
  };

  return MiddlewareDefinition;

})();

module.exports = MiddlewareDefinition;
